import ExpenseItem from './ExpenseItem'
import './ExpensesItemList.css'
import Card from '../UI/Card'
import ExpensesFilter from "./ExpenseFilter";
import {useState} from "react";


function ExpenseItemList(props) {
    const [filterYear, setFilteredYear] = useState('2021');
    const filterYearHandler = selectedYear => {
        setFilteredYear(selectedYear);
        console.log('from expense list', selectedYear)
    }
    return (
        <div>
            <Card className="expenses">
                <ExpensesFilter onChangeFilter={filterYearHandler} selectedYear={filterYear}/>
                {props.expenses.map(expense => {
                    if (filterYear === expense.year) {
                        return <ExpenseItem
                            key={expense.id}
                            title={expense.title}
                            amount={expense.amount}
                            date={expense.date}
                        />
                    } else {
                        return <ExpenseItem
                            key={expense.id}
                            title={expense.title}
                            amount={expense.amount}
                            date={expense.date}
                        />
                    }
                })}
            </Card>
        </div>


    )
}

export default ExpenseItemList
